    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        // TODO add your handling code here:
        String nome = txtNome.getText();
        String estilo = txtEstilo.getText();
        String pais = txtPais.getText();
        Musica b = new Musica(this.codigo,nome,estilo,pais);
        b.imprime();
        if(this.btnCadastrar.getText().equals("EDITAR")) {
            b.atualizar();
        } else {
            b.insere();            
        }
        this.janelaPai.getBandas().atualiza();
        this.dispose();
        JOptionPane.showMessageDialog(null, "Cadastrado realizado com sucesso!");
    }//GEN-LAST:event_btnCadastrarActionPerformed

