    public void editar(int codigo) {
        Musica b = new Musica(codigo);
        b.imprime();
        this.txtNome.setText(b.getNome());
        this.txtEstilo.setText(b.getEstilo());
        this.txtPais.setText(b.getPais());
        this.btnCadastrar.setText("EDITAR");
        this.codigo = codigo;
    }
    
